[
    {
        "_key": "901@1.0.0@1.0.0",
        "id": "901@1.0.0",
        "cfg": "1.0.0",
        "desc": "Number of outgoing transactions - debtor",
        "config": {
            "parameters": {
                "timeframe": 86400000
            },
            "exitConditions": [
                {
                    "subRuleRef": ".x00",
                    "outcome": false,
                    "reason": "Incoming transaction is unsuccessful"
                }
            ],
            "bands": [
                {
                    "subRuleRef": ".01",
                    "upperLimit": 2,
                    "outcome": true,
                    "reason": "Debtor made less than two transactions"
                },
                {
                    "subRuleRef": ".02",
                    "lowerLimit": 2,
                    "upperLimit": 3,
                    "outcome": true,
                    "reason": "Debtor made two transactions"
                },
                {
                    "subRuleRef": ".03",
                    "lowerLimit": 3,
                    "outcome": true,
                    "reason": "Debtor made three or more transactions"
                }
            ]
        }
    }
]

